import React from 'react'
import { styled } from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';

function History() {
   const {tranacctionHistory}=useGlobalContext 

   const [...history] = tranacctionHistory;

  return (
    < HistoryStyled>
    <h2>Recent History</h2>
    {history.map((transaction) => {
      const { _id, title, amount, type, category, date, createdAt } = transaction;
      return (
        <div className="history-item" key={_id}>
          <p style={{
            color: type === 'expense' ? 'red' : 'green'

          }}> {title}</p>

           <p style={{
            color: type === 'expense' ? 'red' : 'green'

          }}>
            {
                type === 'expense' ? '-' : '+'
            }
            

          Rs{amount}</p>

          


          <p>{category}</p>
          <p>{date}</p>
          
          <button className={`status ${type}`}>{type}</button>
        </div>
      );
    })}
    </ HistoryStyled>
  )
}

const HistoryStyled = styled.div`
display: flex;
flex-direction: column;
gap: 1rem;
.history-item{
    display: flex;
    align-items: center;
    justify-content: space-between;
    background: #fcf6f9;
    padding: 1rem;
    border-radius: 1rem;
    .status{
        background: #fcf6f9;
        padding: 0.5rem;
        border-radius: 1rem;
        font-size: 0.8rem;
        text-transform: capitalize;
        &.expense{
            color: red;
        }
        &.income{
            color: green;
        }
    }
} 

 
 

`;

export default History
