import React from 'react'
import { styled } from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import { dateFormat } from '../../utilis/dateFormat';

function History() {
   const {tranacctionHistory}=useGlobalContext(); 

   const [...history] = tranacctionHistory();

  return (
    < HistoryStyled>
    <h2>Recent History</h2>
    {history.map((transaction) => {
      const { _id, title, amount, type, category, date, createdAt } = transaction;
      return (
        <div className="history-item" key={_id}>
          <p style={{
            color: type === 'expense' ? 'red' : 'green'

          }}> {title}</p>

           <p style={{
            color: type === 'expense' ? 'red' : 'green'

          }}>
            {
                type === 'expense' ? '-' : '+'
            }
            

          Rs{amount}</p>

          


          <p>{category}</p>
          <p>{dateFormat(date)}</p>
          
          
        </div>
      );
    })}
    </ HistoryStyled>
  )
}

const HistoryStyled = styled.div`
display: flex;
flex-direction: column;
gap: 1rem;
.history-item{
    background: #FCF6F9;
    border :2px solid #FFFFFF
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    font
    padding: 1rem;
    border-radius: 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    
} 

 
 

`;

export default History
